import groovy.json.JsonSlurper

responseContent = testRunner.testCase.getTestStepByName("createWorkDay").getPropertyValue("response")
slurperresponse = new JsonSlurper().parseText(responseContent)
log.info (slurperresponse.actualDay)


testRunner.testCase.setPropertyValue("WorkDayDate", slurperresponse.actualDay);
testRunner.testCase.setPropertyValue("WorkDayYear", slurperresponse.actualDay.substring(0,4));
testRunner.testCase.setPropertyValue("WorkDayMonth", slurperresponse.actualDay.substring(5,7));
